import "../App.css";
import balor from "../img/balor.jpeg";
import gametable from "../img/gametable.jpeg";
import mountainView from "../img/mountian-view.jpeg";
import skullManor from "../img/skull-manor.jpeg";
import waterBattle from "../img/water-battle.jpeg";


const ImageSlider = () => {

var right = 0;
var maxMargin;
var jumpMargin = 150;

function setWidth(){
  var boxwidth = document.querySelector(".sliderContainer").offsetWidth;
  var displaywidth = document.querySelector(".row").offsetWidth;
  var displayheight = document.querySelector(".row").offsetHeight;
  var children = document.querySelectorAll(".row-container > .sliderContainer").length;
  var outerboxwidth = children * boxwidth + (children*10);
  document.querySelector(".row-container").style.width = outerboxwidth+"px";
  document.querySelectorAll("sliderButton")[0].style.height = displayheight+"px";
  document.querySelectorAll("sliderButton")[1].style.height = displayheight+"px";
  maxMargin = outerboxwidth - displaywidth;
}

function slideLeft(){
  var rowcont = document.querySelector(".row-container");
  if(right <= -maxMargin){

  }
  else{
    right -= jumpMargin;
  }
   rowcont.style.marginLeft = right+"px";
}

function slideRight(){
  var rowcont = document.querySelector(".row-container");
  if(right===0){

  }
  else if(right >= maxMargin){

  }
  else{
     right += jumpMargin;
  }
  rowcont.style.marginLeft = right+"px";
}

window.onload={setWidth};


    return (
        <div className="carousel">
  <button className='flt-left, sliderButton' onClick={slideRight()}><i className="fa fa-angle-left" aria-hidden="true"></i></button>
  <div className="row flt-left">

   <div className="row-container">
  <div className="sliderContainer">
    <img src={balor} alt="balor" />
  </div>
  <div className="sliderContainer">
    <img src={waterBattle} alt="sdf"></img>
  </div>
  <div className="sliderContainer">
    <img src={skullManor} alt="sdg"/>
  </div>
      <div className="sliderContainer">
    <img src={mountainView} alt="sg"/>
  </div>
     <div className="sliderContainer">
    <img src={gametable} alt="sg"/>
  </div>
    <div className="clear"></div>
    </div>

    </div>
   <button className='flt-left sliderButton'  onClick={slideLeft()}><i className="fa fa-angle-right" aria-hidden="true"></i></button>
    <div className="clear"></div>
</div>
    );
};

export default ImageSlider;
