import React, { useEffect,useState } from "react";

const data = window.localStorage.getItem('TavernItems');

function CostComponent(props) {
    const [selectedValue, setSelectedValue] = useState([]);
    useEffect(() => {
    if (data !== null) setSelectedValue(JSON.parse(data))
  }, []);
    const handleCostChange = (event) => {
        const id = String(event.target.dataset.id);
        const val = String(event.target.value);
        const items = JSON.parse(window.localStorage.getItem('TavernItems'));
        const match = items.find(x => x.id === id);

        function updateItems() {
            window.localStorage.setItem('TavernItems', JSON.stringify(items));
        };


        function updateCost() {
            delete match.cost;
            match.cost = val;

        }
        if (match && val) {
            console.log("im here")
            updateCost()
            updateItems();   

        }

    }

    return (
        (selectedValue).map((option) => (
            <span key={option.id} className="itemCost">
                <input className="cost" data-id={option.id} value={option.cost} onChange={handleCostChange} type="number" id="cost" name="cost" />
                <div className="cost-dropdown">
                    <div className="cost-select">
                        <span className="cost-selected">GP</span>
                        <div className="cost-caret"></div>
                    </div>
                    <ul className="cost-menu">
                        <li className="cost-active">GP</li>
                        <li>PP</li>
                        <li>EP</li>
                        <li>SP</li>
                        <li>CP</li>
                    </ul>
                </div>
            </span>

        ))
    );
}

export default CostComponent;
