import React, { useState, useEffect } from 'react';
import '../App.css';
import { useParams } from 'react-router-dom';
import axios from "axios";
const API_URL = "http://localhost:8080/";

function ItemDetail() {

    const [item, setItem] = useState([]);
    const id = useParams();
    const [desc, setDesc] = useState([]);

    useEffect(() => {
        fetchItem()
    }, []);


    const fetchItem = async () => {
        try {
            const fetchItem = await fetch(`https://www.dnd5eapi.co/api/${id.url}/${id.id}`);
            console.log(fetchItem.status);
            const item = await fetchItem.json();
            const desc = item.desc;
            setItem(item); 
            setDesc(desc);
        } catch (err) {

            if (fetchItem.status === 404) {
                const data = await axios.get(API_URL + 'api/pub');
                const items = await data.data;
                const item = (items.filter(x => x.index === `${id.id}`)[0])
                setItem(item); 
                return;
            } else {
                console.error();
                return;
            }
        }     
        } 


        return (
            <div>
                <h1 className='title'>
                    {item.name}
                </h1>
                <div className='text'>
                    <div className='itemType'>
                        {item.desc}
                        {desc.length === 0 &&
                            <div>
                                No description available, ask your DM for more inforomation.
                            </div>
                        }
                    </div>
                </div>
            </div>
        );

};




export default ItemDetail;
