import React, {useState, useEffect} from 'react';
import Dropdown from './dropdown.component';
import uniqid from 'uniqid';
import axios from "axios";


const userData = window.localStorage.getItem('user');
const API_URL = "http://localhost:8080/";

function Shop() {
    useEffect(() => {
        fetchItems();
    }, []);

    useEffect(() => {
    fetchShopItems();
    }, []);

const fetchShopItems = async () => {
    const id = (JSON.parse(userData)).uniqid;
    const response = await axios.get(API_URL + "api/fetch/" + id);
        //console.log(response.data[0].shops)
        const items = JSON.parse(response.data[0].shops);


    window.localStorage.setItem('ITEMS', JSON.stringify(items));

  };



    const [items, setItems] = useState([]);

    const fetchItems = async () => {
        const data = await fetch(
            'https://www.dnd5eapi.co/api/magic-items'
        )
        const items = await data.json();
        //console.log(items.results);
        setItems(items.results);
    };

    const options = items.map((item) => {
        return {
            value: item.name,
            label: item.name,
            url: item.url,
            id: uniqid()
        };

    }, [])

    return (
        <div className='App'key={items.id}>
    <h1 className='title'>Gilmore's Shop</h1>

            <Dropdown
                    isSearchable
                    isMulti
                    placeHolder="Select..."
                    options={options}
                    onChange={(value)=> console.log(value)}
                />

        </div>
  );
}



export default Shop;
