import React, {useState, useEffect} from 'react';
import Dropdown from './dropdown.component';
import uniqid from 'uniqid';
import fetchItems from './fetch.component';

function Tavern() {
    useEffect(() => {
        fetchTavernItems();
    }, []);

    const [items, setItems] = useState([]);

    const fetchTavernItems = async () => {
        const data = await fetch(
            'https://www.dnd5eapi.co/api/magic-items'
        )
        const items = await data.json();
        //console.log(items.results);
        setItems(items.results);
    };

    const options = items.map((item) => {
        return {
            value: item.name,
            label: item.name,
            url: item.url,
            id: uniqid()
        };

    }, [])

    return (
        <div className='App'key={items.id}>
    <h1 className='title'>Tavern Creator</h1>

            <Dropdown
                    isSearchable
                    isMulti
                    placeHolder="Select..."
                    options={options}
                    onChange={(value)=> console.log(value)}
                />
     <buttton><button onClick={async () => {
        const run = 
          await fetchItems();
        window.location.reload();
        return run;
      }}>Open Tavern</button></buttton>
        </div>
  );
}



export default Tavern;
