import React, { useState, useEffect } from 'react';
import '../App.css';
import { useParams } from 'react-router-dom';

function ItemDetail() {
    useEffect(() => {
        fetchItem();
    });

    const [item, setItem] = useState([]);

    const id = useParams();
    //console.log(id);

    const fetchItem = async () => {
        const fetchItem = await fetch(`https://www.dnd5eapi.co/api/${id.url}/${id.id}`);
        const item = fetchItem.json();
        setItem(item); 
    };

    const ifNone = async (option) => {
        await fetchItem();
        //console.log();
        if (option.length === 0) {
            return (
                <div>
                    Description Unavailable
                </div>

            );
        } else {

            return (
                <div>

                </div>
            );

        };
    };

    return (
        <div>
            <h1 className='title'>
                {item.name}
            </h1>
            <div className='text'>
                <div className='itemType'>
                    {ifNone(item.desc)}
                </div>
            </div>
        </div>
    );
}




export default ItemDetail;
