import React, {useState, useEffect} from 'react';
import TavernDropdown from './tavern.dropdown.component';
import uniqid from 'uniqid';
import axios from "axios";
import { Link } from 'react-router-dom';
import '../App.css';


function Tavern() {
    useEffect(() => {
        fetchTavernItems();
    }, []);

    const [items, setItems] = useState([]);

const userData = window.localStorage.getItem('user');
const API_URL = "http://localhost:8080/";




const fetchItems = async () => {

    const id = (JSON.parse(userData)).uniqid;
    const response = await axios.get(API_URL + "api/fetch/" + id);
        //console.log(response.data[0].shops)
    const items = JSON.parse(response.data[0].taverns);


    window.localStorage.setItem('TavernItems', JSON.stringify(items));

  };






    const fetchTavernItems = async () => {
        const data = await fetch(
            'https://www.dnd5eapi.co/api/equipment'
        )
        const items = await data.json();
        //console.log(items.results);
        setItems(items.results);
    };

    const options = items.map((item) => {
        return {
            value: item.name,
            label: item.name,
            url: item.url,
            id: uniqid(),
            cost: 0,
            coinType: "GP"
        };

    }, [])

    return (
        <div className='App'key={items.id}>
    <h1 className='title'>Tavern Creator</h1>

            <TavernDropdown
                    isSearchable
                    isMulti
                    placeHolder="Select..."
                    options={options}
                    onChange={(value)=> console.log(value)}
                />
     <button className='buttons' onClick={async () => {
        const run = 
          await fetchItems();
        window.location.reload();
        return run;
            }}>Open Tavern</button>
            <button><Link className='buttons' to={`/tavern/${uniqid()}`}>Publish Tavern</Link> </button>
        </div>
  );
}



export default Tavern;
