import axios from "axios";

const API_URL = "http://localhost:8080/api/auth/";
const userData = window.localStorage.getItem('user');
const API_URL_2 = "http://localhost:8080/";

const fetchItems = async () => {
    const id = (JSON.parse(userData)).uniqid;
    const response = await axios.get(API_URL_2 + "api/fetch/" + id);
        //console.log(response.data[0].shops)
        const items = JSON.parse(response.data[0].shops);


    window.localStorage.setItem('ITEMS', JSON.stringify(items));

  };





class AuthService {
  login(username, password) {
    return axios
      .post(API_URL + "signin", {
        username,
        password
      })
      .then(response => {
        if (response.data.accessToken) {
          localStorage.setItem("user", JSON.stringify(response.data));
          fetchItems();
        }

        return response.data;
      });
  }

  logout() {
    localStorage.removeItem("user");
    localStorage.removeItem("ITEMS");
  }

  register(username, email, password) {
    return axios.post(API_URL + "signup", {
      username,
      email,
      password
    });
  }

  getCurrentUser() {
    return JSON.parse(localStorage.getItem('user'));;
  }
}

export default new AuthService();
