import React, { Component } from "react";
//import ImageSlider from "./img-slider.components";
import UserService from "../services/user.service";
import Carousel from "./img-slider/img-slider.components";

import balor from "../img/balor.jpeg";
import gametable from "../img/gametable.jpeg";
import mountainView from "../img/mountian-view.jpeg";
import skullManor from "../img/skull-manor.jpeg";
import waterBattle from "../img/water-battle.jpeg";

export default class Home extends Component {
  constructor(props) {
    super(props);

    this.state = {
      content: ""
    };
  }

  componentDidMount() {
    UserService.getPublicContent().then(
      response => {
        this.setState({
          content: response.data
        });
      },
      error => {
        this.setState({
          content:
            (error.response && error.response.data) ||
            error.message ||
            error.toString()
        });
      }
    );

  }



  render() {
    return (
      <div className="homeContainer">
        <header className="homeHeader">
          <h3>{this.state.content}</h3>
        </header>
        <div>
          <p className="homeIntro">
            This site is a passion project to help make the worlds we build more immersive,
            by taking out the time of monotonously reading off shop or pub items. Instead
            players can window shop your world just like they would do their favorite IRL
            stores.
          </p>
        </div>
        <div className="imgSliderContainer">
        <Carousel
                show={3}
            >
                <div>
                    <div style={{padding: 8}} >
                        <img src={balor} alt="placeholder" style={{width:'100%'}} />
                    </div>
                </div>
                <div>
                    <div style={{padding: 8}}>
                        <img src={waterBattle} alt="placeholder"  />
                    </div>
                </div>
                <div>
                    <div style={{padding: 8}}>
                        <img src={gametable} alt="placeholder"  />
                    </div>
                </div>
                <div>
                    <div style={{padding: 8}}>
                        <img src={mountainView} alt="placeholder"  />
                    </div>
                </div>
                <div>
                    <div style={{padding: 8}}>
                        <img src={skullManor} alt="placeholder"  />
                    </div>
                </div>
            </Carousel>

        </div>



      </div>
    );
  }
}
