import React, { useEffect, useState } from "react";
import axios from "axios";
import authHeader from '../services/auth-header';

const API_URL = "http://localhost:8080/api/test/";
const data = window.localStorage.getItem('ITEMS')

const SaveItems = () => {
  const [selectedValue, setSelectedValue] = useState();
   useEffect(() => {
    if (data !== null) setSelectedValue(JSON.parse(data))   
  }, []);


  useEffect(() => {
    window.localStorage.setItem('ITEMS', JSON.stringify(selectedValue))
  }, [selectedValue]);

    axios.post(API_URL + "saveItems", { headers: authHeader() },  {
      label: selectedValue,
      url: selectedValue,
      value: selectedValue
    });
    console.log("items Saved")
};

export default SaveItems;
